WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s2.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, (SELECT trg as src, src as trg, label FROM edge) as s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = 5 AND s1.label = 3  AND s2.label = 1 )) , c1(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 5)) , c2(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 5)) SELECT DISTINCT c0.src, c2.src FROM c0, c1, c2 WHERE c2.src = c0.trg AND c0.src = c1.src;
